<script>
import { useTemplateRef, onMounted, onUnmounted } from 'vue';

import { initialize as initializeGameEngine } from '../engines/threeJS/main';
import { useUserStore } from '../stores/user';
import { useGameStore } from '../stores/game';
import GameControls from '../components/GameControls.vue';
import OverlayLogin from '../components/OverlayLogin.vue';
import OverlaySelectModule from '../components/OverlaySelectModule.vue';
import CharacterSelection from '../components/CharacterSelection.vue';
import './Home.css';

export default {
  name: 'ShahkarClient',
  components: {
    GameControls,
    OverlayLogin,
    OverlaySelectModule,
    CharacterSelection,
  },
  setup() {
    const userStore = useUserStore();
    const gameStore = useGameStore();
    const gameCanvas = useTemplateRef('gameCanvas');

    onMounted(async () => {
      if (gameCanvas.value) {
        await initializeGameEngine(gameCanvas.value, gameStore.board.camera.position);
        document.addEventListener('keydown', function (event) {
          gameStore.handleInteraction(event, 'keyboard');
        });
        gameCanvas.value.addEventListener('mousemove', function (event) {
          gameStore.handleInteraction(event, 'mousemove');
        });
        gameCanvas.value.addEventListener('click', function (event) {
          gameStore.handleInteraction(event, 'mouseclick');
        });
      }
    });

    onUnmounted(() => {
      document.removeEventListener('keydown', gameStore.handleInteraction);
    });

    return {
      userStore,
      gameCanvas,
    };
  },
};
</script>

